      difficulty: 'Medium',
      description: `Given an array of integers, determine if by removing at most one element the array can become strictly increasing.`,
      testcases: [
        { input: '[1,2,10,5,7]', output: 'YES', hidden: false },
        { input: '[2,3,1,2]', output: 'NO', hidden: false },
        { input: '[1,1,1]', output: 'NO', hidden: true },
        { input: '[1,2,3,7,5,8]', output: 'YES', hidden: true },
        { input: '[3,4,2,3]', output: 'NO', hidden: true },
        { input: '[1,3,6,7,9,8]', output: 'YES', hidden: true }
      ]
    }
  ],

  round2: [
    {
      id: 'r2p1',
      title: 'Longest Increasing Subsequence',
      difficulty: 'Hard',
      description: `Find the length of the longest strictly increasing subsequence.`,
      testcases: [
        { input: '[10,9,2,5,3,7,101,18]', output: '4', hidden: false }
      ]
    }
  ]
};

