import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useContest } from '../contexts/ContestContext';
import CodeEditor from '../components/CodeEditor';
import TestcaseResults from '../components/TestcaseResults';
import Timer from '../components/Timer';
import { Button } from '../components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '../components/ui/card';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '../components/ui/select';
import { Textarea } from '../components/ui/textarea';
import { ScrollArea } from '../components/ui/scroll-area';
import { Badge } from '../components/ui/badge';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '../components/ui/tabs';
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from '../components/ui/dialog';
import { toast } from '../hooks/use-toast';
import { Code2, Play, Send, AlertCircle, CheckCircle2 } from 'lucide-react';
import { languageOptions } from '../mock';

const CodingPage = () => {
  const { roundId } = useParams();
  const { user } = useAuth();
  const { getRoundInfo, submitCode, runCode } = useContest();
  const navigate = useNavigate();

  const roundData = getRoundInfo(roundId);

  // problems are now fetched from backend (instead of relying only on roundData.problems)
  const [problems, setProblems] = useState(roundData?.problems || []);
  const [loadingProblems, setLoadingProblems] = useState(false);

  const [selectedProblemId, setSelectedProblemId] = useState(
    (roundData?.problems?.[0]?.id) || (roundData?.problems?.[0]?._id) || null
  );
  const [language, setLanguage] = useState('python');
  const [code, setCode] = useState(languageOptions[0].template);
  const [customInput, setCustomInput] = useState('');
  const [runOutput, setRunOutput] = useState('');
  const [testcaseResults, setTestcaseResults] = useState([]);
  const [isRunning, setIsRunning] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [timeExpired, setTimeExpired] = useState(false);
  const [showTimeUpModal, setShowTimeUpModal] = useState(false);

  // Fetch problems from backend on mount
  useEffect(() => {
    let mounted = true;
    async function loadProblems() {
      setLoadingProblems(true);
      try {
        const res = await fetch(`${process.env.REACT_APP_BACKEND_URL}/problems`);
        if (!res.ok) {
          console.error('Failed to fetch problems:', res.status, await res.text());
          setProblems(roundData?.problems || []); // fallback to roundData
        } else {
          const data = await res.json();
          // handle different response shapes: raw array or { problems: [...] }
          const list = data?.problems || data;
          if (mounted) {
            setProblems(list || []);
            // set default selectedProblem if none set yet
            if ((!selectedProblemId) && list && list.length > 0) {
              const first = list[0];
              setSelectedProblemId(first.id || first._id);
            }
          }
        }
      } catch (err) {
        console.error('Error fetching problems:', err);
        if (mounted) setProblems(roundData?.problems || []);
      } finally {
        if (mounted) setLoadingProblems(false);
      }
    }

    loadProblems();
    return () => { mounted = false; };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [roundId]); // refetch when roundId changes

  useEffect(() => {
    if (!roundData || roundData.status !== 'active') {
      navigate('/dashboard');
    }
  }, [roundData, navigate]);

  useEffect(() => {
    // Set default code template when language changes
    const selectedLang = languageOptions.find(l => l.value === language);
    if (selectedLang) {
      setCode(selectedLang.template);
    }
  }, [language]);

  const handleTimeUp = () => {
    setTimeExpired(true);
    setShowTimeUpModal(true);
  };

  const handleRun = async () => {
    setIsRunning(true);
    setRunOutput('Running...');

    try {
      const result = await runCode(code, language, customInput);
      if (result && result.success) {
        setRunOutput(result.output);
        toast({
          title: "Code executed successfully",
          description: `Execution time: ${result.executionTime?.toFixed?.(2) ?? 'N/A'}ms`,
        });
      } else {
        // handle failure response
        setRunOutput(result?.output || 'Execution failed');
      }
    } catch (error) {
      setRunOutput('Error: ' + (error.message || error));
    }

    setIsRunning(false);
  };

  const handleSubmit = async () => {
    const currentProblem = problems.find(p => (p.id || p._id) === selectedProblemId);
    if (!currentProblem) return;

    setIsSubmitting(true);

    try {
      const result = await submitCode(user.id, (currentProblem.id || currentProblem._id), code, language, roundId);
      if (result && result.success) {
        const subsRes = await fetch(`${process.env.REACT_APP_BACKEND_URL}/submissions/${user.id}`);
        const subs = subsRes.ok ? await subsRes.json() : [];
        const latest = Array.isArray(subs) ? subs[0] : null;
        const results = latest?.results || [];
        setTestcaseResults(results);
        const passedCount = results.filter(r => r.passed).length;
        toast({
          title: "Code submitted successfully",
          description: `Passed ${passedCount}/${results.length} testcases`,
        });
      } else {
        throw new Error(result?.message || 'Submission failed');
      }
    } catch (error) {
      toast({
        title: "Submission failed",
        description: error.message || String(error),
        variant: "destructive",
      });
    }

    setIsSubmitting(false);
  };

  if (!roundData || (!selectedProblemId && !loadingProblems)) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  // derive current problem from id for stability across fetches
  const currentProblem = (problems && problems.length)
    ? problems.find(p => (p.id || p._id) === selectedProblemId) || problems[0]
    : null;

  return (
    <div className="h-screen flex flex-col bg-gray-50 overflow-hidden">
      {/* Top Bar */}
      <div className="bg-white border-b border-gray-200 px-4 py-3 flex items-center justify-between shadow-sm">
        <div className="flex items-center space-x-4">
          <Code2 className="h-6 w-6 text-blue-600" />
          <div>
            <h1 className="font-bold text-lg">{roundData?.name}</h1>
            <p className="text-xs text-gray-600">{user?.name}</p>
          </div>
        </div>

        <div className="flex items-center space-x-4">
          <Timer
            startTime={roundData?.startTime}
            duration={roundData?.duration}
            onTimeUp={handleTimeUp}
          />

          <Button
            onClick={handleSubmit}
            disabled={isSubmitting || timeExpired}
            className="bg-green-600 hover:bg-green-700"
          >
            <Send className="h-4 w-4 mr-2" />
            {isSubmitting ? 'Submitting...' : 'Submit'}
          </Button>

          <Button variant="outline" onClick={() => navigate('/dashboard')}>
            Exit
          </Button>
        </div>
      </div>

      {/* Main Content Area - Single Screen Layout */}
      <div className="flex-1 flex overflow-hidden">
        {/* Left Panel - Problem Statement */}
        <div className="w-1/4 border-r border-gray-200 bg-white flex flex-col">
          <div className="p-4 border-b border-gray-200">
            <h2 className="font-semibold text-sm text-gray-600 mb-2">Problems</h2>
            <div className="space-y-2">
              {loadingProblems ? (
                <div className="text-sm text-gray-500">Loading problems...</div>
              ) : problems && problems.length > 0 ? (
                problems.map((problem, idx) => (
                  <button
                    key={problem.id || problem._id || idx}
                    onClick={() => setSelectedProblemId(problem.id || problem._id)}
                    className={`w-full text-left p-2 rounded-lg transition-colors ${
                      currentProblem && (currentProblem.id || currentProblem._id) === (problem.id || problem._id)
                        ? 'bg-blue-100 border border-blue-300'
                        : 'bg-gray-50 hover:bg-gray-100'
                    }`}
                  >
                    <div className="flex items-center justify-between">
                      <span className="text-sm font-medium">{idx + 1}. {problem.title}</span>
                      <Badge variant={problem.difficulty === 'easy' || problem.difficulty === 'Easy' ? 'default' : 'secondary'}>
                        {problem.difficulty || 'easy'}
                      </Badge>
                    </div>
                  </button>
                ))
              ) : (
                <div className="text-sm text-gray-500">No problems found.</div>
              )}
            </div>
          </div>

          <ScrollArea className="flex-1 p-4">
            <div className="prose prose-sm max-w-none">
              {currentProblem ? (
                <>
                  <h3 className="text-lg font-bold mb-2">{currentProblem.title}</h3>
                  <Badge className="mb-4">{currentProblem.difficulty}</Badge>
                  <div className="whitespace-pre-wrap text-sm text-gray-700">
                    {currentProblem.description}
                  </div>
                </>
              ) : (
                <div className="text-sm text-gray-500">Select a problem to view details.</div>
              )}
            </div>
          </ScrollArea>
        </div>

        {/* Center Panel - Code Editor */}
        <div className="flex-1 flex flex-col bg-gray-900">
          <div className="bg-gray-800 px-4 py-2 flex items-center justify-between border-b border-gray-700">
            <div className="flex items-center space-x-4">
              <span className="text-white text-sm font-medium">Code Editor</span>
              <Select value={language} onValueChange={setLanguage} disabled={timeExpired}>
                <SelectTrigger className="w-40 bg-gray-700 text-white border-gray-600">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {languageOptions.map((lang) => (
                    <SelectItem key={lang.value} value={lang.value}>
                      {lang.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            {timeExpired && (
              <Badge variant="destructive" className="animate-pulse">
                <AlertCircle className="h-3 w-3 mr-1" />
                Time Expired
              </Badge>
            )}
          </div>

          <div className="flex-1">
            <CodeEditor
              value={code}
              onChange={(value) => setCode(value)}
              language={language}
              readOnly={timeExpired}
            />
          </div>

          {/* Bottom Bar - Custom Input & Run */}
          <div className="bg-gray-800 border-t border-gray-700 p-3">
            <div className="flex items-start space-x-3">
              <div className="flex-1">
                <label className="text-xs text-gray-400 mb-1 block">Custom Input</label>
                <Textarea
                  value={customInput}
                  onChange={(e) => setCustomInput(e.target.value)}
                  placeholder="Enter custom input here..."
                  className="bg-gray-700 text-white border-gray-600 h-20 text-sm font-mono"
                  disabled={timeExpired}
                />
              </div>
              <div className="flex-1">
                <label className="text-xs text-gray-400 mb-1 block">Output</label>
                <div className="bg-gray-700 text-white rounded-md p-2 h-20 overflow-auto text-sm font-mono whitespace-pre-wrap">
                  {runOutput || 'Click Run to see output'}
                </div>
              </div>
              <Button
                onClick={handleRun}
                disabled={isRunning || timeExpired}
                variant="secondary"
                className="mt-5"
              >
                <Play className="h-4 w-4 mr-2" />
                {isRunning ? 'Running...' : 'Run'}
              </Button>
            </div>
          </div>
        </div>

        {/* Right Panel - Testcase Results & Output */}
        <div className="w-1/4 border-l border-gray-200 bg-white flex flex-col">
          <Tabs defaultValue="testcases" className="flex-1 flex flex-col">
            <TabsList className="w-full rounded-none border-b">
              <TabsTrigger value="testcases" className="flex-1">
                Testcase Results
              </TabsTrigger>
            </TabsList>
            <TabsContent value="testcases" className="flex-1 m-0 p-4">
              <TestcaseResults results={testcaseResults} />
            </TabsContent>
          </Tabs>
        </div>
      </div>

      {/* Time Up Modal */}
      <Dialog open={showTimeUpModal} onOpenChange={setShowTimeUpModal}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle className="flex items-center space-x-2 text-red-600">
              <AlertCircle className="h-6 w-6" />
              <span>Time's Up!</span>
            </DialogTitle>
            <DialogDescription>
              The round has ended. Your editor is now locked and you can no longer submit code.
              You can review your code, but no further submissions are allowed.
            </DialogDescription>
          </DialogHeader>
          <div className="flex justify-end space-x-2">
            <Button variant="outline" onClick={() => navigate('/dashboard')}>
              Go to Dashboard
            </Button>
            <Button onClick={() => setShowTimeUpModal(false)}>
              Review Code
            </Button>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
};

export default CodingPage;
