import React, { createContext, useContext, useEffect, useState } from 'react';
import { languageOptions } from '../mock';
import { api } from '../utils/api';

const ContestContext = createContext();

export const useContest = () => {
  const context = useContext(ContestContext);
  if (!context) {
    throw new Error('useContest must be used within ContestProvider');
  }
  return context;
};

export const ContestProvider = ({ children }) => {
  const [roundInfo, setRoundInfo] = useState({});
  const [participants, setParticipants] = useState([]);
  const [submissions, setSubmissions] = useState([]);

  const getRoundInfo = (roundId) => roundInfo[roundId];

  const updateRoundStatus = (roundId, status) => {
    setRoundInfo((prev) => ({
      ...prev,
      [roundId]: { ...prev[roundId], status }
    }));
  };

  const startRound = (roundId) => {
    setRoundInfo((prev) => ({
      ...prev,
      [roundId]: {
        ...prev[roundId],
        status: 'active',
        startTime: new Date().toISOString(),
        isLocked: false
      }
    }));
  };

  const lockRound = (roundId) => {
    setRoundInfo((prev) => ({
      ...prev,
      [roundId]: { ...prev[roundId], isLocked: true }
    }));
  };

  const unlockRound = (roundId) => {
    setRoundInfo((prev) => ({
      ...prev,
      [roundId]: { ...prev[roundId], isLocked: false }
    }));
  };

  const toggleEligibility = (participantId) => {
    setParticipants((prev) =>
      prev.map((p) =>
        p.id === participantId ? { ...p, round2Eligible: !p.round2Eligible } : p
      )
    );
  };

  const submitCode = async (userId, problemId, code, language, round) => {
    const payload = { userId, problemId, code, language, round };
    const { data } = await api.submitCode(payload);
    const { submissionId } = data;
    const result = { success: true, submissionId };
    try {
      const { data: subs } = await api.getUserSubmissions(userId);
      setSubmissions(subs || []);
    } catch {}
    return result;
  };

  const runCode = async (code, language, customInput) => {
    const { data } = await api.runCode({ code, language, customInput });
    const run = data?.run || {};
    return {
      success: true,
      output: run.output || run.stdout || '',
      executionTime: run.cpu_time || null,
    };
  };

  useEffect(() => {
    async function bootstrap() {
      const rounds = ['round1', 'round2'];
      const next = {};
      for (const id of rounds) {
        try {
          const { data } = await api.getRoundWindow(id);
          next[id] = { ...data, id, name: id === 'round1' ? 'Round 1' : 'Round 2' };
        } catch {
          next[id] = { id, name: id, status: 'upcoming', isLocked: true };
        }
      }
      try {
        const { data } = await api.getProblems();
        const list = data?.problems || data || [];
        next.round1 = { ...(next.round1 || {}), problems: list };
        next.round2 = { ...(next.round2 || {}), problems: list };
      } catch {}
      setRoundInfo(next);
    }
    bootstrap();
  }, []);

  return (
    <ContestContext.Provider
      value={{
        roundInfo,
        participants,
        submissions,
        getRoundInfo,
        updateRoundStatus,
        startRound,
        lockRound,
        unlockRound,
        toggleEligibility,
        submitCode,
        runCode
      }}
    >
      {children}
    </ContestContext.Provider>
  );
};
