import React, { createContext, useContext, useState } from 'react';
import { mockRoundInfo, mockProblems, mockParticipants } from '../mock';

const ContestContext = createContext();

export const useContest = () => {
  const context = useContext(ContestContext);
  if (!context) {
    throw new Error('useContest must be used within ContestProvider');
  }
  return context;
};

export const ContestProvider = ({ children }) => {
  const [roundInfo, setRoundInfo] = useState(mockRoundInfo);
  const [participants, setParticipants] = useState(mockParticipants);
  const [submissions, setSubmissions] = useState([]);

  const getRoundInfo = (roundId) => roundInfo[roundId];

  const updateRoundStatus = (roundId, status) => {
    setRoundInfo((prev) => ({
      ...prev,
      [roundId]: { ...prev[roundId], status }
    }));
  };

  const startRound = (roundId) => {
    setRoundInfo((prev) => ({
      ...prev,
      [roundId]: {
        ...prev[roundId],
        status: 'active',
        startTime: new Date().toISOString(),
        isLocked: false
      }
    }));
  };

  const lockRound = (roundId) => {
    setRoundInfo((prev) => ({
      ...prev,
      [roundId]: { ...prev[roundId], isLocked: true }
    }));
  };

  const unlockRound = (roundId) => {
    setRoundInfo((prev) => ({
      ...prev,
      [roundId]: { ...prev[roundId], isLocked: false }
    }));
  };

  const toggleEligibility = (participantId) => {
    setParticipants((prev) =>
      prev.map((p) =>
        p.id === participantId ? { ...p, round2Eligible: !p.round2Eligible } : p
      )
    );
  };

  const submitCode = (userId, problemId, code, language, round) => {
    const problem = mockProblems[round].find((p) => p.id === problemId);
    if (!problem) return { success: false, error: 'Problem not found' };

    const passedCount = Math.floor(Math.random() * (problem.testcases.length + 1));

    const submission = {
      id: 's' + Date.now(),
      userId,
      problemId,
      round,
      code,
      language,
      timestamp: new Date().toISOString(),
      testcasesPassed: passedCount,
      totalTestcases: problem.testcases.length
    };

    setSubmissions((prev) => [...prev, submission]);

    setParticipants((prev) =>
      prev.map((p) =>
        p.id === userId
          ? { ...p, submissions: [...(p.submissions || []), submission] }
          : p
      )
    );

    return {
      success: true,
      submission,
      testcaseResults: problem.testcases.map((tc, idx) => ({
        testcase: idx + 1,
        passed: idx < passedCount,
        input: tc.input,
        expectedOutput: tc.output,
        actualOutput: idx < passedCount ? tc.output : 'Wrong Answer',
        hidden: tc.hidden
      }))
    };
  };

  const runCode = (code, language, customInput) => ({
    success: true,
    output: `Execution completed.\nLanguage: ${language}\nCustom Input: ${customInput || 'None'}\n[Mock Output]`,
    executionTime: Math.random() * 100
  });

  return (
    <ContestContext.Provider
      value={{
        roundInfo,
        participants,
        submissions,
        getRoundInfo,
        updateRoundStatus,
        startRound,
        lockRound,
        unlockRound,
        toggleEligibility,
        submitCode,
        runCode
      }}
    >
      {children}
    </ContestContext.Provider>
  );
};
